package day6_test

import (
	"testing"

	"github.com/merkata/go-aoc-2022/day6"
)

type test struct {
	description string
	input       string
	distinct    int
	want        int
}

func TestGetMarker(t *testing.T) {
	tests := []test{
		{description: "part 1 validation",
			distinct: 3,
			input:    "mjqjpqmgbljsphdztnvjfqwrcgsmlb",
			want:     7},
		{description: "part 1 additional validation",
			distinct: 3,
			input:    "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg",
			want:     10},
		{description: "part 1 solution",
			distinct: 3,
			input:    "",
			want:     1034},
		{description: "part 2 validation",
			distinct: 13,
			input:    "mjqjpqmgbljsphdztnvjfqwrcgsmlb",
			want:     19},
		{description: "part 2 additional validation",
			distinct: 13,
			input:    "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg",
			want:     29},
		{description: "part 2 solution",
			distinct: 13,
			input:    "",
			want:     2472},
	}
	for _, tc := range tests {
		got := day6.MarkerPoint(tc.input, tc.distinct)
		if tc.want != got {
			t.Errorf("failed test: %s, wanted %d got %d\n", tc.description, tc.want, got)
		}
	}
}
